mapper.select:
SELECT HALL_ID, SEAT_NUM FROM BOOKSEAT BS
WHERE BS.SHOW_ID = #{show_id}
ORDER BY SEAT_NUM

SELECT COUNT(BOOK_ID) FROM BOOK B


        AND B.USER_ID = #{user_id}



        AND TO_CHAR(B.BOOK_DATETIME, 'YYYYMMDD') >= #{start_date}



        AND TO_CHAR(B.BOOK_DATETIME, 'YYYYMMDD') <= #{end_date}

SELECT B.BOOK_ID, B.BOOK_PAY_ID, B.USER_NAME, B.BOOK_DATETIME, B.SHOW_DATE, B.SHOW_TIME, B.MOVI_NAME, B.HALL_NAME FROM ( SELECT /*+index_desc(IDX_BOOK_PK)*/ ROWNUM AS RNUM, A.BOOK_ID, A.BOOK_PAY_ID, A.USER_NAME, A.BOOK_DATETIME, A.SHOW_DATE, A.SHOW_TIME, A.MOVI_NAME, A.HALL_NAME FROM( SELECT BK.BOOK_ID, BK.BOOK_PAY_ID, U.USER_NAME, BK.BOOK_DATETIME, S.SHOW_DATE, S.SHOW_TIME, M.MOVI_NAME, H.HALL_NAME
FROM BOOK BK INNER JOIN SHOWSCHEDULE S on S.SHOW_ID = BK.SHOW_ID INNER JOIN MOVIE M on S.MOVI_ID = M.MOVI_ID INNER JOIN HALL H on S.HALL_ID = H.HALL_ID INNER JOIN USERS U ON U.USER_ID = BK.USER_ID
AND BK.USER_ID = #{user_id}
AND TO_CHAR(BK.BOOK_DATETIME, 'YYYYMMDD') >= #{start_date}
AND TO_CHAR(BK.BOOK_DATETIME, 'YYYYMMDD') <= #{end_date}
) A WHERE ROWNUM <= #{page}*#{amount}) B WHERE B.RNUM > (#{page}-1)*#{amount}
