mapper.update:
UPDATE SHOWSCHEDULE SET MOVI_ID = #{movi_id},
                        HALL_ID = #{hall_id},
                        SHOW_DATE = #{show_date},
                        SHOW_TIME = #{show_time}
WHERE SHOW_ID =  #{show_id}

mapper.select:
SELECT COUNT(SHOW_ID) FROM SHOWSCHEDULE S




        AND S.SHOW_DATE >= #{start_date}



        AND S.SHOW_DATE <= #{end_date}



        AND S.MOVI_ID IN

            #{id}




        AND S.HALL_ID IN

            #{id}

SELECT B.RNUM, B.SHOW_ID, B.MOVI_ID, B.MOVI_NAME, B.HALL_ID, B.HALL_NAME, B.SHOW_DATE, B.SHOW_TIME, B.END_TIME, B.AVAI_SEAT_AMOUNT, B.HALL_SEAT FROM ( SELECT /*+index_desc(IDX_SHOWSCHEDULE_PK)*/ ROWNUM AS RNUM, A.SHOW_ID, A.MOVI_ID, A.MOVI_NAME, A.HALL_ID, A.HALL_NAME, A.SHOW_DATE, A.SHOW_TIME, A.END_TIME, A.AVAI_SEAT_AMOUNT, A.HALL_SEAT FROM( SELECT S.SHOW_ID, S.MOVI_ID, S.HALL_ID, S.SHOW_DATE, S.SHOW_TIME, M.MOVI_NAME, M.RUN_TIME AS END_TIME, H.HALL_NAME, H.AVAI_SEAT_AMOUNT, H.HALL_ROW* H.HALL_COL AS HALL_SEAT
FROM SHOWSCHEDULE S INNER JOIN HALL H ON S.HALL_ID = H.HALL_ID INNER JOIN MOVIE M ON M.MOVI_ID = S.MOVI_ID
AND S.SHOW_DATE >= #{start_date}
AND S.SHOW_DATE <= #{end_date}
AND S.MOVI_ID IN
#{id}
AND S.HALL_ID IN
#{id}
) A WHERE ROWNUM <= #{page}*#{amount}) B WHERE B.RNUM > (#{page}-1)*#{amount}

mapper.update:
UPDATE BOOKPAY BP SET BP.PAY_STATE_CODE = #{pay_stat_fin},
                      BP.USE_DATETIME = SYSDATE
WHERE BP.BOOK_PAY_ID IN (
    SELECT B.BOOK_PAY_ID FROM BOOK B
    INNER JOIN SHOWSCHEDULE S on B.SHOW_ID = S.SHOW_ID
    WHERE S.SHOW_ID = #{show_id}
    )
AND BP.PAY_STATE_CODE = #{pay_stat_ok}
