mapper.update:
UPDATE PRODUCTPAY SET PAY_STATE_CODE = #{pay_state_fin},
                      USE_DATETIME = SYSDATE
WHERE USE_CODE = #{use_code}

mapper.select:
SELECT COUNT(PROD_PAY_ID) FROM PRODUCTPAY P


        AND P.USER_ID = #{user_id}



        AND TO_CHAR(P.PAY_DATETIME, 'YYYYMMDD') >= #{start_date}



        AND TO_CHAR(P.PAY_DATETIME, 'YYYYMMDD') <= #{end_date}

SELECT B.PROD_PAY_ID, B.USER_ID, B.USE_POINT, B.PRICE, B.ACCU_POINT, B.PAY_TYPE, B.PAY_STATE, B.PAY_DATETIME, B.USE_DATETIME, B.USE_CODE FROM ( SELECT /*+index_desc(IDX_BOOK_PK)*/ ROWNUM AS RNUM, A.PROD_PAY_ID, A.USER_ID, A.USE_POINT, A.PRICE, A.ACCU_POINT, A.PAY_TYPE, A.PAY_STATE, A.PAY_DATETIME, A.USE_DATETIME, A.USE_CODE
FROM( SELECT P.PROD_PAY_ID, P.USER_ID, P.USE_POINT, P.PRICE, P.ACCU_POINT, CC.CODE_NAME AS PAY_TYPE, C.CODE_NAME AS PAY_STATE, P.PAY_DATETIME, P.USE_DATETIME, P.USE_CODE FROM PRODUCTPAY P INNER JOIN CODE C ON P.PAY_STATE_CODE = C.CODE_ID INNER JOIN CODE CC ON P.PAY_TYPE_CODE = CC.CODE_ID
AND P.USER_ID = #{user_id}
AND TO_CHAR(P.PAY_DATETIME, 'YYYYMMDD') >= #{start_date}
AND TO_CHAR(P.PAY_DATETIME, 'YYYYMMDD') <= #{end_date}
ORDER BY P.PROD_PAY_ID ) A WHERE ROWNUM <= #{page}*#{amount}) B WHERE B.RNUM > (#{page}-1)*#{amount}
